      /**
 * jCarouselLite - jQuery plugin to navigate images/any content in a carousel style widget.
 * @requires jQuery v1.2 or above
 *
 * https://gmarwaha.com/jquery/jcarousellite/
 *
 * Copyright (c) 2007 Ganeshji Marwaha (gmarwaha.com)
 * Duplo licenciamento de MIT e GPL licenças:
 * https://www.opensource.org/licenses/mit-license.php
 * https://www.gnu.org/licenses/gpl.html
 *
 * Version: 1.0.1
 * Note: Requires jquery 1.2 or above from version 1.0.1
 */
      (function($) {                                          // Em conformidade com jquery.noConflict()
        $.fn.jCarouselLite = function(o) {
          o = $.extend({
            btnPrev: null,
            btnNext: null,
            btnGo: null,
            mouseWheel: false,
            auto: null,
            speed: 200,
            easing: null,
            vertical: false,
            circular: true,
            visible: 4,
            start: 0,
            scroll: 1,
            beforeStart: null,
            afterEnd: null
          }, o || {});
          return this.each(function() {                           // Retorna a coleção elemento. Chainable.
            var running = false, animCss=o.vertical?"top":"left", sizeCss=o.vertical?"height":"width";
            var div = $(this), ul = $("ul", div), tLi = $("li", ul), tl = tLi.size(), v = o.visible;
            if(o.circular) {
              ul.prepend(tLi.slice(tl-v-1+1).clone())
              .append(tLi.slice(0,v).clone());
              o.start += v;
            }
            var li = $("li", ul), itemLength = li.size(), curr = o.start;
            div.css("visibility", "visible");
            li.css({overflow: "hidden", float: o.vertical ? "none" : "left"});
            ul.css({margin: "0", padding: "0", position: "relative", "list-style-type": "none", "z-index": "1"});
            div.css({overflow: "hidden", position: "relative", "z-index": "2", left: "0px"});
            var liSize = o.vertical ? height(li) : width(li);   // tamanho da LI completa (incluindo margem)-Usada para animações
            var ulSize = liSize * itemLength;                   // tamanho da UL completo (comprimento total, não apenas para os itens visíveis)
            var divSize = liSize * v;                           // tamanho do DIV inteira (comprimento total por apenas os itens visíveis)
            li.css({width: li.width(), height: li.height()});
            ul.css(sizeCss, ulSize+"px").css(animCss, -(curr*liSize));
            div.css(sizeCss, divSize+"px");                     // Largura do DIV. comprimento de imagens visíveis
            if(o.btnPrev)
              $(o.btnPrev).click(function() {
                return go(curr-o.scroll);
              });
            if(o.btnNext)
              $(o.btnNext).click(function() {
                return go(curr+o.scroll);
              });
            if(o.btnGo)
              $.each(o.btnGo, function(i, val) {
                $(val).click(function() {
                  return go(o.circular ? o.visible+i : i);
                });
              });
            if(o.mouseWheel && div.mousewheel)
              div.mousewheel(function(e, d) {
                return d>0 ? go(curr-o.scroll) : go(curr+o.scroll);
              });
            if(o.auto)
              setInterval(function() {
                go(curr+o.scroll);
              }, o.auto+o.speed);
            function vis() {
              return li.slice(curr).slice(0,v);
            };
            function go(to) {
              if(!running) {
                if(o.beforeStart)
                  o.beforeStart.call(this, vis());
                if(o.circular) {            // Se a circular estiver em primeiro ou último, em seguida, vá para o outro lado
                  if(to<=o.start-v-1) {           // Se em primeiro lugar, em seguida, ir ao último
                    ul.css(animCss, -((itemLength-(v*2))*liSize)+"px");
                    // If "scroll" > 1, em seguida, o "to" pode não ser igual à condição; pode ser menor, dependendo do número de elementos .
                    curr = to==o.start-v-1 ? itemLength-(v*2)-1 : itemLength-(v*2)-o.scroll;
                  } else if(to>=itemLength-v+1) { // If last, then goto first
                    ul.css(animCss, -( (v) * liSize ) + "px" );
                    // If "scroll" > 1, em seguida, o "to" pode não ser igual à condição; ela pode ser maior, dependendo do número de elementos .
                    curr = to==itemLength-v+1 ? v+1 : v+o.scroll;
                  } else curr = to;
                } else {                    // Se não circular e aponta para primeiro ou o último, resta apenas voltar.
                  if(to<0 || to>itemLength-v) return;
                  else curr = to;
                }                           // Se nem o substitui, o curr ainda será "to" e podemos continuar.
                running = true;
                ul.animate(
                  animCss == "left" ? { left: -(curr*liSize) } : { top: -(curr*liSize) } , o.speed, o.easing,
                  function() {
                    if(o.afterEnd)
                      o.afterEnd.call(this, vis());
                    running = false;
                  }
                );
                // Desativar botões quando o carrossel atinge a última / primeira, e permitir que quando não
                if(!o.circular) {
                  $(o.btnPrev + "," + o.btnNext).removeClass("disabled");
                  $( (curr-o.scroll<0 && o.btnPrev)
                    ||
                    (curr+o.scroll > itemLength-v && o.btnNext)
                    ||
                    []
                   ).addClass("disabled");
                }
              }
              return false;
            };
          });
        };
        function css(el, prop) {
          return parseInt($.css(el[0], prop)) || 0;
        };
        function width(el) {
          return  el[0].offsetWidth + css(el, 'marginLeft') + css(el, 'marginRight');
        };
        function height(el) {
          return el[0].offsetHeight + css(el, 'marginTop') + css(el, 'marginBottom');
        };
      })(jQuery);
