$(document).ready(function() {
  // alterar a variável de dimensão abaixo para ser o tamanho do pixel que você quer
  var dimension = 100;
  // este elemento identifica o PopularPosts1 div , encontrar cada imagem nele, e redimensiona-lo
  $('#PopularPosts1').find('img').each(function(n, image){
    var image = $(image);
    image.attr({src : image.attr('src').replace(/s\B\d{2,4}/,'s' + dimension)});
    image.attr('width',dimension);
    image.attr('height',dimension);
  });
});
